<script setup>
import { useMessageStore } from '@/store/useMessageStore'
import { gsap } from 'gsap'
import { ref, watch } from 'vue'
import { sendEmail } from '../../axios/axios'
// import { nextTick } from 'vue';
const phoneNumber = ref('')
const email = ref('')
const emailError = ref('')
const store = useMessageStore()
const sendEmailStatus = ref('')
const spinning = ref(false)
const isContactButton = ref(false)
const phoneError = ref('')
const toClose = () => {
  // 创建一个缩小动画，将消息容器从当前大小缩小为0，持续时间为1秒
  gsap.to('.message-container', {
    duration: 0.5,
    x: 389,
    onComplete: () => {
      // 在动画完成后执行关闭窗口的逻辑
      store.isChatVisible = !store.isChatVisible
      store.isEmailBox = false
      // nextTick(() => {
      //     // 执行另一个动画，例如放大动画
      //     gsap.from('.right-box', {
      //         duration: 1,
      //         y: 10,
      //     });
      // })
    }
  })
}
watch(sendEmailStatus, (newStatus) => {
  if (newStatus == 'sucess') {
    // 等待几秒钟后将 sendEmailStatus.value 设置为空字符串
    setTimeout(() => {
      sendEmailStatus.value = ''
      store.isEmailBox = !store.isEmailBox
    }, 3000) // 这里的3000表示3秒，你可以根据需要调整时间
  }
})
//发送邮箱手机
const contactEmali = async () => {
  const data = {
    phone: phoneNumber.value,
    email: email.value,
    subject: '咨询'
  }
  try {
    if (
      (phoneNumber.value && phoneError.value === '') ||
      (email.value && emailError.value === '')
    ) {
      spinning.value = true
      const res = await sendEmail(data)
      isContactButton.value = true
      if (res.data.code == 1) {
        console.log('success')
        sendEmailStatus.value = 'sucess'
        phoneNumber.value = ''
        email.value = ''
      } else {
        sendEmailStatus.value = 'error'
        spinning.value = false
      }
      spinning.value = false
      isContactButton.value = false
    }
  } catch (error) {
    console.log(error)
  }
}
const toggleEmail = () => {
  console.log(store.isEmailBox)
  store.isEmailBox = !store.isEmailBox
  phoneNumber.value = ''
  email.value = ''
  emailError.value = ''
  phoneError.value = ''
  isContactButton.value = false
  spinning.value = false
}
//验证
const validatePhoneNumber = () => {
  sendEmailStatus.value = ''
  // Add your phone number validation logic here
  if (!/^\d{11}$/.test(phoneNumber.value)) {
    phoneError.value = 'Invalid phone number'
  } else {
    phoneError.value = ''
  }
}

const validateEmail = () => {
  sendEmailStatus.value = ''
  // Add your email validation logic here
  if (!/^\S+@\S+\.\S+$/.test(email.value)) {
    emailError.value = 'Invalid email address'
  } else {
    emailError.value = ''
  }
}
</script>

